mapper.select:
SELECT scope,
       entity_id,
       field,
       default_locale,
       translations,
       updated_at
FROM mt_multilingual_texts
WHERE scope = #{scope}
  AND entity_id = #{entityId}
  AND field = #{field}

mapper.update:
UPDATE mt_multilingual_texts
SET default_locale = #{defaultLocale},
    translations = #{translations, typeHandler=com.bob.mta.common.mybatis.StringMapJsonTypeHandler},
    updated_at = #{updatedAt}
WHERE scope = #{scope}
  AND entity_id = #{entityId}
  AND field = #{field}
